// colours
$knob-colour: #181818;
$light-colour: #a8d8f8;

.knob-surround {
  position: relative;
  background-color: #181818;
  width: 6em;
  height: 6em;
  border-radius: 50%;
  border: solid .25em darken($knob-colour, 4%);
  margin: auto; 
  gradient:$knob-colour, lighten($knob-colour, 2%), darken($knob-colour, 2%);
  box-shadow:
    0 .2em .1em .05em rgba(255,255,255,.1) inset,
    0 -.2em .1em .05em rgba(0,0,0,.5) inset,
    0 .5em .65em 0 rgba(0,0,0,.3);
}

.knob {
  position: absolute;
  width: 100%;
  height: 100%;
  border-radius: 50%;
  transform: rotate(0deg);
  z-index: 10;
}

.knob:before {
  content: "";
  position: absolute;
  bottom: 19%;
  left: 19%;
  width: 3%;
  height: 3%;
  background-color: $light-colour;
  border-radius: 50%;
  box-shadow: 0 0 .4em 0 darken($light-colour, 10%);
}

.min,
.max {
  display: block;
  font-family: "Varela Round", sans-serif;
  color: rgb(255,242,181);
  text-transform: uppercase;
  -webkit-font-smoothing: antialiased;
  font-size: 50%;
  position: absolute;
  opacity: .5;
}

.min {
  bottom: 0em;
  left: -5.5em;
}

.max {
  bottom: 0em;
  right: -5.5em;
}

.tick {
  position: absolute;
  width: 100%;
  height: 100%;
  top: 0;
  left: 0;
  z-index: 5;
  overflow: visible;
}

.tick:after {
  content: "";
  width: .08em;
  height: .6em;
  background-color: rgb(255,242,181);
  position: absolute;
  top: -1.5em;
  left: 50%;
  transition: all 180ms ease-out;
}

.activetick:after {
  background-color: $light-colour;
  box-shadow: 0 0 .3em .08em darken($light-colour, 10%);
  transition: all 50ms ease-in;
}


